var Intensity = 1 //Positive values are a push, neative values are a pull.
var MaxRange = 6
var DistanceScalar = -100 //How the push/pull effect will be amplified based on the distance between npc and target. Can be negative. 0 for no effect
var Speed = 0 //How fast the wave goes out. Higher is slower
var Particle = "instantSpell" // You can edit specifics further down in the script

var AbilityAttemptTime = 0 //seconds

function init(t){
t.npc.timers.forceStart(1,AbilityAttemptTime*20,true)}

function timer(t){
if(t.id==1){
CircleAttack(t.npc) 
}}

function CircleAttack(npc){
var Thread = Java.type("java.lang.Thread");
var HankThread = Java.extend(Thread,{run: function(){
var vics = npc.world.getNearbyEntities(npc.x,npc.y,npc.z,MaxRange,-1)
for(var j = 0;j<vics.length;++j){
if(vics[j]!=npc){
var dist = npc.getPos().distanceTo(vics[j].getPos())
var Str = Intensity + DistanceScalar*dist
var d = FrontVectors(npc,GetPlayerRotation(npc,vics[j]),0,Intensity,0)
vics[j].setMotionX(d[0])
vics[j].setMotionY(0.1)
vics[j].setMotionZ(d[2])}}
if(Intensity > 0){
for(var k = 0; k< MaxRange*2;++k){
for(var i = 0; i<72;++i){
var d = FrontVectors(npc,i*5,0,k/2*(Intensity/Math.abs(Intensity)),0)
npc.world.spawnParticle(Particle,npc.x+d[0],npc.y,npc.z+d[2],0,0,0,0,1)}
Thread.sleep(Speed)}}
else{
for(var k = MaxRange*2; k>0 ;--k){
for(var i = 0; i<72;++i){
var d = FrontVectors(npc,i*5,0,k/2*(Intensity/Math.abs(Intensity)),0)
npc.world.spawnParticle(Particle,npc.x+d[0],npc.y,npc.z+d[2],0,0,0,0,1)}
Thread.sleep(Speed)}}
}});var H = new HankThread();H.start();}

function GetPlayerRotation(npc, player){
var dx = npc.getX()-player.getX();
var dz = player.getZ()-npc.getZ();
if(dz>=0){
var angle = (Math.atan(dx/dz)*180/Math.PI);
if(angle < 0){
angle = 360+angle;}}
if(dz<0){
dz = -dz;
var angle = 180-(Math.atan(dx/dz)*180/Math.PI);}
return angle;}

function FrontVectors(entity,dr,dp,distance,mode){
if(mode == 1){var angle = dr + entity.getRotation();var pitch = (-entity.getPitch()+dp)*Math.PI/180}
if(mode == 0){var angle = dr;var pitch = (dp)*Math.PI/180}
var dx = -Math.sin(angle*Math.PI/180)*(distance*Math.cos(pitch))
var dy = Math.sin(pitch)*distance
var dz = Math.cos(angle*Math.PI/180)*(distance*Math.cos(pitch))
return [dx,dy,dz]}

function damaged(t){
if(t.damageSource.isProjectile() && t.source){
if(!t.npc.isAttacking())t.npc.setAttackTarget(t.source)
t.npc.world.playSoundAt(t.npc.getPos(),"minecraft:entity.blaze.shoot",5,1)
t.npc.world.spawnParticle("largesmoke",t.npc.x,t.npc.y+1,t.npc.z,0.3,0.5,0.3,0,25)
var TrueSource = t.damageSource.getImmediateSource()
t.setCanceled(true)
var nbt = TrueSource.getEntityNbt()
var x =nbt.getList("Motion",6)[0]
var y =nbt.getList("Motion",6)[1]
var z =nbt.getList("Motion",6)[2]
var Speed = Math.sqrt(x*x+y*y+z*z)
var d = FrontVectors(t.npc,GetPlayerRotation(t.npc,t.source),GetPlayerPitch(t.npc,t.source),Speed/2,0)
nbt.setList("Motion",[d[0],d[1],d[2]])
var newProj = t.npc.world.createEntityFromNBT(nbt)
newProj.generateNewUUID()
t.npc.world.spawnEntity(newProj)
TrueSource.despawn()}}

function FrontVectors(entity,dr,dp,distance,mode){
if(!mode)mode=0
if(mode == 1){var angle = dr + entity.getRotation();var pitch = (-entity.getPitch()+dp)*Math.PI/180;if(dp==0)pitch=0;}
if(mode == 0){var angle = dr;var pitch = (dp)*Math.PI/180}
var dx = -Math.sin(angle*Math.PI/180)*(distance*Math.cos(pitch))
var dy = Math.sin(pitch)*distance
var dz = Math.cos(angle*Math.PI/180)*(distance*Math.cos(pitch))
return [dx,dy,dz]}

function GetPlayerRotation(npc, player){
var dx = npc.getX()-player.getX();
var dz = player.getZ()-npc.getZ();
if(dz>=0){
var angle = (Math.atan(dx/dz)*180/Math.PI);
if(angle < 0){
angle = 360+angle;}}
if(dz<0){
dz = -dz;
var angle = 180-(Math.atan(dx/dz)*180/Math.PI);}
return angle;}

function GetPlayerPitch(npc,player){
var distance = Math.sqrt(Math.pow((npc.x - player.x),2)+Math.pow((npc.z - player.z),2))
var dy = player.y-npc.y
var pitch = Math.atan(dy/distance)*180/Math.PI
return pitch}

var LaunchSpeed = 10;
var CloneNameList = ["Blue", "Red", "Hollow Purple"]
var CloneName
var ServerClonesTab = 1
var AutoTarget = true //manually set the projectile NPCs attack target to the NPC's attack target

function randomNum(max, min) {
    return Math.random() * (max - min) + min
}

function rangedLaunched(t){
var randomNumber = randomNum(3, 1);

if(randomNumber = 1){
CloneName = CloneNameList[0]
}
else if(randomNumber = 2){
CloneName = CloneNameList[1]
}
else if(randomNumber = 3){
CloneName = CloneNameList[2]
}

var npc = t.npc
var targ = npc.getAttackTarget()
if(!targ)return;
var shot = t.projectiles[0]
shot.despawn()
var clone = t.API.clones.spawn(npc.x+23,npc.y+1.5,npc.z+23,ServerClonesTab,CloneName,npc.world)
var d = FrontVectors(npc,GetPlayerRotation(npc, targ),GetPlayerPitch(npc,targ)+5,LaunchSpeed,0)
clone.setMotionX(d[0])
clone.setMotionY(d[1])
clone.setMotionZ(d[2])
if(AutoTarget)clone.setAttackTarget(targ)}

